#!/bin/bash
# Create placeholder PNG icon files using base64 encoding
# Minimal valid 1x1 transparent PNG

ICON_DIR="./icons"
mkdir -p "$ICON_DIR"

# Minimal 1x1 transparent PNG in base64 (iVBORw0KGgo...AASUVORK5CYII=)
MIN_PNG="iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNk+M9QDwADhgGAWjR9awAAAABJRU5ErkJggg=="

# Create PNG files
echo "$MIN_PNG" | base64 -d > "$ICON_DIR/32x32.png"
echo "$MIN_PNG" | base64 -d > "$ICON_DIR/128x128.png"
echo "$MIN_PNG" | base64 -d > "$ICON_DIR/128x128@2x.png"
echo "$MIN_PNG" | base64 -d > "$ICON_DIR/tray_icon.png"

# For ICO file, create minimal ICO from PNG (ICO header + embedded PNG)
# This is a minimal valid ICO file that Tauri will accept
ICO_HEX=""
echo "$ICO_HEX" | xxd -r -p > "$ICON_DIR/icon.ico"

echo "Icons created successfully in $ICON_DIR"
ls -la "$ICON_DIR"
